def res_secondPostalAddressId = context.expand( '${Display Postal Addresses of Sales Organization#res_secondPostalAddressId}' ).toString();


//Getting input values
def req_state1 = context.expand( '${Add postal address item (first)#req_state}' ).toString();
def req_state2 = context.expand( '${Add postal address item (second)#req_state}' ).toString();

if(res_firstState.equals(req_state1)) 
{
testRunner.testCase.testSteps['Postal AddressId Infos'].setPropertyValue("PostalAddressId1", res_firstPostalAddressId.toString());
testRunner.testCase.testSteps['Postal AddressId Infos'].setPropertyValue("PostalAddressId2", res_secondPostalAddressId.toString());
}
else if(res_firstState.equals(req_state2))
{
testRunner.testCase.testSteps['Postal AddressId Infos'].setPropertyValue("PostalAddressId1", res_secondPostalAddressId.toString());
testRunner.testCase.testSteps['Postal AddressId Infos'].setPropertyValue("PostalAddressId2", res_firstPostalAddressId.toString());
}
