import net.sf.json.groovy.*;

def firstAmount = context.expand( '${Get account balance informations for health-check#res_firstAmount}' )
def firstCurrency = context.expand( '${Get account balance informations for health-check#res_firstCurrency}' )
def secondAmount = context.expand( '${Get account balance informations for health-check#res_secondAmount}' )
def secondCurrency = context.expand( '${Get account balance informations for health-check#res_secondCurrency}' )

def accountBalanceList = context.expand( '${Get account balance informations for health-check#response}' )
def json = new JsonSlurper().parseText (accountBalanceList);
	//Check first element of index for USD
	if (json[0].amount.currency == "USD") 
	{	//Swap amounts between from and two
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_firstAmount",secondAmount);
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_secondAmount",firstAmount);
		//Swap currency between from and two
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_firstCurrency",secondCurrency);
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_secondCurrency",firstCurrency);				
	}
	if (firstAmount=="200.0")
	{
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_firstAmount","200");
		testRunner.testCase.testSteps['Get account balance informations for health-check'].setPropertyValue("res_secondAmount","100");
	}
